li {
  list-style: none;
}

/* font-family: 'Noto Sans KR', sans-serif; */
body {
  font-family: "Noto Sans KR", sans-serif;
  font-weight: 500;
}

/************countdown popup*************/
.countdown {
  width: 400px;
  height: 550px;
  background-color: #eee;
  margin-top: 100px;
  position: absolute;
  z-index: 9999;
  top: 250px;
  left: 180px;
  border-radius: 10px;
  display: block;
}
.countdown h1 {
  text-align: center;
  font-size: 28px;
  padding: 0px 10px;
  margin-top: 10px;
  padding-bottom: 10px;
  border-bottom: 2px solid #ccc;
}

.countdown_box {
  margin: 20px auto;
}
.countdown_box .time p {
  text-align: center;
  font-size: 25px;
  margin-top: 30px;
}
.countdown_box .count_img {
  width: 100%;
  font-size: 0;
  margin-top: 20px;
}
.countdown_box .count_img video {
  width: 100%;
  height: 350px;
  border-radius: 20px;
}

.close_btn {
  text-align: right;
  font-size: 25px;
  cursor: pointer;
}
.close_btn i {
  margin-right: 10px;
}

