/***********search box****************/
.search_box {
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(15px);
  position: fixed;
  top: 0px;
  z-index: 99999;
  display: none;
}
.search_box .search_box_wrap {
  width: 100%;
  background-color: #fff;
  padding: 30px 0;
}
.search_box .search_input {
  width: 100%;
  position: relative;
  padding-top: 30px;
}
.search_box .search_input input {
  width: 50%;
  height: 60px;
  margin: 0 auto;
  display: block;
  border-radius: 30px;
  outline: none;
  border: 2px solid #ccc;
  background-color: #eee;
  font-size: 20px;
  padding: 0 10px;
}
.search_box .search_input .search_close {
  width: 50px;
  height: 50px;
  font-size: 25px;
  position: absolute;
  right: 0;
  top: 25px;
  cursor: pointer;
}
.search_box .search_list_wrap {
  display: flex;
  margin-top: 30px;
}
.search_box .search_list_wrap .search_list {
  width: 33.3333%;
  border-right: 1px solid #ccc;
  padding-left: 100px;
}
.search_box .search_list_wrap .search_list:last-child {
  border-right: 0;
}
.search_box .search_list_wrap .search_list h4 {
  font-size: 20px;
  margin: 30px 0;
}
.search_box .search_list_wrap .search_list li {
  margin-bottom: 15px;
}
.search_box .search_list_wrap .recent_search li:first-child {
  color: #888;
}
.search_box .search_list_wrap .recent_search .delete_history span {
  font-size: 14px;
  color: #888;
  text-decoration: underline;
  display: block;
  margin-top: 50px;
  cursor: pointer;
}

/*************header*************/
header {
  width: 100%;
  position: fixed;
  top: 0;
  z-index: 999;
  height: 70px;
  line-height: 70px;
}
header:hover {
  background-color: #fff;
}
header:hover a {
  color: black !important;
}

.head_up {
  position: absolute;
}

.head_down {
  position: fixed;
}

.header_wrap {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.head_logo {
  width: 33.3333%;
}
.head_logo .imgwrap {
  width: 90px;
  margin-left: 25px;
  margin-top: 10px;
}
.head_logo .imgwrap img {
  width: 100%;
  height: auto;
}

.head_lnb {
  width: 33.3333%;
}
.head_lnb ul {
  display: flex;
  justify-content: space-around;
}
.head_lnb ul li {
  font-size: 20px;
  font-weight: bold;
}
.head_lnb ul li:nth-child(4) {
  position: relative;
}
.head_lnb ul li:nth-child(4)::before {
  content: "";
  display: block;
  width: 1px;
  height: 14px;
  background-color: #ccc;
  position: absolute;
  top: 25px;
  left: -35px;
}
.head_lnb ul li a {
  display: block;
  width: 100%;
  color: #fff;
  position: relative;
  transition: 0.3s;
}
.head_lnb ul li a:hover {
  color: #002053;
}
.head_lnb ul li a:hover::after, .head_lnb ul li a:hover::before {
  width: 50%;
}
.head_lnb ul li a::after {
  display: block;
  content: "";
  width: 0%;
  height: 2px;
  background-color: #002053;
  position: absolute;
  bottom: 13px;
  right: 50%;
  transition: 0.3s;
}
.head_lnb ul li a::before {
  display: block;
  content: "";
  width: 0%;
  height: 2px;
  background-color: #002053;
  position: absolute;
  bottom: 13px;
  left: 50%;
  transition: 0.3s;
}

.head_gnb {
  width: 33.3333%;
}
.head_gnb ul {
  display: flex;
  justify-content: flex-end;
}
.head_gnb ul li {
  margin-right: 25px;
  position: relative;
}
.head_gnb ul li .count {
  width: 20px;
  height: 20px;
  line-height: 20px;
  display: block;
  background-color: #002053;
  border-radius: 50%;
  text-align: center;
  color: #fff;
  font-size: 13px;
  position: absolute;
  top: 10px;
  right: -5px;
}
.head_gnb ul li a {
  width: 100%;
  display: block;
  font-size: 25px;
  color: #fff;
  transition: 0.3s;
}

.header:hover .head_lnb ul li a, .header:hover .head_gnb ul li a {
  color: black;
}

/********drop down menu*********/
.dropdown_menu {
  width: 100%;
  position: absolute;
  top: 70px;
  z-index: 100;
  background-color: #fff;
  display: none;
  padding: 20px;
}

.drop_menu_wrap {
  width: 100%;
  display: flex;
}

.drop_kids .dbox {
  width: 20%;
}
.drop_kids .dbox:last-child {
  border: 0;
}

.dbox {
  width: 16.66667%;
  border-right: 1px solid #ccc;
  padding-left: 30px;
  padding-top: 10px;
}
.dbox h4 {
  font-size: 22px;
  margin-bottom: 25px;
}

.dropbox_06 {
  border-right: 0;
}

.menu_list {
  width: 100%;
}
.menu_list li {
  height: 20px;
  margin-bottom: 20px;
}
.menu_list li:hover a {
  color: #002053;
  font-weight: bold;
}
.menu_list li a {
  display: block;
  width: 100%;
}

.sub_menu {
  margin-top: 50px;
}
.sub_menu li {
  margin-bottom: 10px;
  font-weight: bold;
}
.sub_menu li a {
  display: block;
  width: 100%;
}

.drop_brand .drop_menu_wrap .brand_box {
  width: calc(20% - 24px);
  margin: 50px 20px;
}
.drop_brand .drop_menu_wrap .brand_box .brand_img {
  width: 100%;
  overflow: hidden;
  font-size: 0;
}
.drop_brand .drop_menu_wrap .brand_box .brand_img img {
  width: 100%;
  height: auto;
  transform: scale(1.15);
  transition: 0.5s;
}
.drop_brand .drop_menu_wrap .brand_box .brand_img:hover img {
  transform: scale(1);
}
.drop_brand .drop_menu_wrap .brand_box .brand_tit {
  text-align: center;
  margin-top: 20px;
  font-size: 20px;
}

/*************visual main*************/
.visual_main {
  width: 100%;
  height: 100%;
}

.swiper {
  width: 100%;
  height: 100%;
  position: relative;
}

.swiper-horizontal > .swiper-pagination-progressbar, .swiper-pagination-progressbar.swiper-pagination-horizontal {
  width: 260px;
  height: 15px;
  top: auto;
  bottom: 94px;
  left: 60px;
  background-color: #fff;
}

.swiper-slide {
  font-size: 0;
  position: relative;
}
.swiper-slide img {
  width: 100%;
  height: 100%;
}
.swiper-slide .visual_tit {
  position: absolute;
  width: 100%;
  bottom: 200px;
  left: 50px;
  z-index: 99999;
}
.swiper-slide .visual_tit p {
  color: #fff;
  font-size: 50px;
}
.swiper-slide .visual_tit a {
  display: block;
  color: #fff;
  font-size: 22px;
  width: 150px;
  height: 50px;
  border-radius: 30px;
  text-align: center;
  line-height: 50px;
  border: 1px solid #fff;
  margin-top: 20px;
  transition: 0.3s;
}
.swiper-slide .visual_tit a:hover {
  background-color: #002053;
  box-shadow: 0 0 15px black;
}

.swiper-pagination-progressbar .swiper-pagination-progressbar-fill {
  background-color: #002053;
}

.swiper-button-prev {
  color: #fff;
  left: 150px;
  /* top: 870px; */
  top: 50%;
  transform: translateY(-50%);
}
.swiper-button-prev::after {
  font-size: 50px;
  border: 1px solid #fff;
  padding: 10px;
  border-radius: 10px;
}

.swiper-button-next {
  color: #fff;
  left: auto;
  right: 150px;
  top: 50%;
  transform: translateY(-50%);
  /* top: 870px; */
}
.swiper-button-next::after {
  font-size: 50px;
  border: 1px solid #fff;
  padding: 10px;
  border-radius: 10px;
}

.swiper-pagination-fraction {
  color: #fff;
  font-size: 20px;
  position: absolute;
  bottom: 17px;
  left: -190px;
}

.pagination_fraction {
  visibility: hidden;
  position: relative;
}
.pagination_fraction .swiper-pagination-current {
  font-size: 30px;
  visibility: visible;
  position: absolute;
  top: -80px;
  left: 220px;
}
.pagination_fraction .swiper-pagination-total {
  font-size: 30px;
  visibility: visible;
  position: absolute;
  top: -80px;
  left: 525px;
}

/********rank*******/
.rank_section {
  width: 100%;
  margin-top: 100px;
}
.rank_section h1 {
  text-align: center;
  margin-top: 20px;
  font-size: 36px;
}

.container_rank {
  /* overflow: hidden; */
  /* position: relative; */
}

.swiper_rank {
  width: 100%;
  overflow: hidden;
  margin-top: 50px;
}
.swiper_rank .swiper-wrapper {
  transition-timing-function: linear;
}
.swiper_rank .swiper-slide {
  width: 30% !important;
  opacity: 0.3;
  transform: scale(0.85);
  transition: 0.3s;
  /* margin-right: 20px; */
  visibility: hidden;
  position: relative;
}
.swiper_rank .swiper-slide.swiper-slide-active {
  opacity: 1;
  transform: scale(1);
  visibility: visible;
}
.swiper_rank .swiper-slide.swiper-slide-prev {
  visibility: hidden;
}
.swiper_rank .swiper-slide.swiper-slide-next {
  visibility: visible;
}
.swiper_rank .swiper-slide img {
  width: 100%;
  height: 700px;
  border-radius: 20px;
}
.swiper_rank .swiper-slide:hover .rank_hover_box {
  opacity: 1;
}
.swiper_rank .swiper-slide .rank_hover_box {
  width: 100%;
  height: 70px;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  position: absolute;
  bottom: 0;
  left: 0;
  border-radius: 0 0 20px 20px;
  opacity: 0;
  transition: 0.3s;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_L {
  width: 70%;
  padding-top: 12px;
  padding-left: 20px;
  cursor: pointer;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_L h4 {
  color: #fff;
  font-size: 18px;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_L p {
  font-size: 16px;
  margin-top: 5px;
  color: #fff;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_R {
  width: 35%;
  font-size: 30px;
  line-height: 70px;
  text-align: right;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_R i {
  margin-right: 20px;
  color: #fff;
  transition: 0.3s;
  cursor: pointer;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_R i:first-child:hover {
  color: red;
}
.swiper_rank .swiper-slide .rank_hover_box .rank_hover_R i:nth-child(2):hover {
  color: #002053;
}

