/*********section sns**********/
.sns_section {
  width: 100%;
  margin-top: 100px;
}
.sns_section h1 {
  text-align: center;
  font-size: 28px;
}

.sns_wrap {
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  margin-top: 50px;
}
.sns_wrap .sns_box {
  width: calc(25% - 50px);
  margin-left: 40px;
  position: relative;
  margin-bottom: 40px;
  transition: 0.3s;
}
.sns_wrap .sns_box:hover {
  box-shadow: 0 0 20px black;
}
.sns_wrap .sns_box::after {
  content: "";
  display: block;
  width: 10px;
  height: 0%;
  background-color: #fff;
  position: absolute;
  bottom: 0;
  left: 0;
  transition: 0.5s;
  z-index: 20;
}
.sns_wrap .sns_box::before {
  content: "";
  display: block;
  width: 0%;
  height: 10px;
  background-color: #fff;
  position: absolute;
  bottom: 0;
  left: 0;
  transition: 0.5s;
  z-index: 20;
}
.sns_wrap .sns_box:hover::after {
  height: 100%;
}
.sns_wrap .sns_box:hover::before {
  width: 100%;
}
.sns_wrap .sns_box:nth-child(4n) {
  margin-right: 40px;
}
.sns_wrap .sns_box .sns_img {
  width: 100%;
  font-size: 0;
}
.sns_wrap .sns_box .sns_img img {
  width: 100%;
  height: auto;
}
.sns_wrap .sns_box .sns_desc {
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  opacity: 0;
  transition: 0.5s;
}
.sns_wrap .sns_box .sns_desc p {
  color: #fff;
  text-align: center;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 18px;
  width: 100%;
  padding: 0 10px;
  /* line-height: 0px; */
}
.sns_wrap .sns_box .sns_desc::after {
  content: "";
  width: 10px;
  height: 0%;
  display: block;
  background-color: #fff;
  position: absolute;
  right: 0;
  top: 0;
  transition: 0.5s;
}
.sns_wrap .sns_box .sns_desc::before {
  content: "";
  width: 0%;
  height: 10px;
  display: block;
  background-color: #fff;
  position: absolute;
  right: 0;
  top: 0;
  transition: 0.5s;
}
.sns_wrap .sns_box:hover .sns_desc {
  opacity: 1;
}
.sns_wrap .sns_box:hover .sns_desc::after {
  height: 100%;
}
.sns_wrap .sns_box:hover .sns_desc::before {
  width: 100%;
}

