li {
  list-style: none;
}

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;400;500;700&display=swap');

/* font-family: 'Noto Sans KR', sans-serif; */

body {
  font-family: 'Noto Sans KR', sans-serif;
  font-weight: 500;
}

/************countdown popup*************/

.countdown {
  width: 400px;
  height: 550px;
  background-color: #eee;
  margin-top: 100px;
  position: absolute;
  z-index: 9999;
  top: 250px;
  left: 180px;
  border-radius: 10px;
  display: block;

  h1 {
    text-align: center;
    font-size: 28px;
    padding: 0px 10px;
    margin-top: 10px;
    padding-bottom: 10px;
    border-bottom: 2px solid #ccc;
  }
}

.countdown_box {
  margin: 20px auto;

  .time p {
    text-align: center;
    font-size: 25px;
    margin-top: 30px;
  }

  .count_img {
    width: 100%;
    font-size: 0;
    margin-top: 20px;

    video {
      width: 100%;
      height: 350px;
      border-radius: 20px;
    }
  }
}

.close_btn {
  text-align: right;
  font-size: 25px;
  cursor: pointer;

  i {
    margin-right: 10px;
  }
}

