/***********search box****************/

.search_box {
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(15px);
  position: fixed;
  top: 0px;
  z-index: 99999;
  display: none;

  .search_box_wrap {
    width: 100%;
    background-color: #fff;
    padding: 30px 0;
  }

  .search_input {
    width: 100%;
    position: relative;
    padding-top: 30px;

    input {
      width: 50%;
      height: 60px;
      margin: 0 auto;
      display: block;
      border-radius: 30px;
      outline: none;
      border: 2px solid #ccc;
      background-color: #eee;
      font-size: 20px;
      padding: 0 10px;

      &[placeholder] {}
    }

    .search_close {
      width: 50px;
      height: 50px;
      font-size: 25px;
      position: absolute;
      right: 0;
      top: 25px;
      cursor: pointer;
    }
  }

  .search_list_wrap {
    display: flex;
    margin-top: 30px;

    .search_list {
      width: 33.3333%;
      border-right: 1px solid #ccc;
      padding-left: 100px;

      &:last-child {
        border-right: 0;
      }

      h4 {
        font-size: 20px;
        margin: 30px 0;
      }

      li {
        margin-bottom: 15px;
      }
    }

    .recent_search {
      li:first-child {
        color: #888;
      }

      .delete_history span {
        font-size: 14px;
        color: #888;
        text-decoration: underline;
        display: block;
        margin-top: 50px;
        cursor: pointer;
      }
    }
  }
}

/*************header*************/

header {
  width: 100%;
  position: fixed;
  top: 0;
  z-index: 999;
  height: 70px;
  line-height: 70px;

  &:hover {
    background-color: #fff;

    a {
      color: black !important;
    }
  }
}

.head_up {
  position: absolute;
}

.head_down {
  position: fixed;
}

.header_wrap {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.head_logo {
  width: 33.3333%;

  .imgwrap {
    width: 90px;
    margin-left: 25px;
    margin-top: 10px;

    img {
      width: 100%;
      height: auto;
    }
  }
}

.head_lnb {
  width: 33.3333%;

  ul {
    display: flex;
    justify-content: space-around;

    li {
      font-size: 20px;
      font-weight: bold;

      &:nth-child(4) {
        position: relative;

        &::before {
          content: "";
          display: block;
          width: 1px;
          height: 14px;
          background-color: #ccc;
          position: absolute;
          top: 25px;
          left: -35px;
        }
      }

      a {
        display: block;
        width: 100%;
        color: #fff;
        position: relative;
        transition: .3s;

        &:hover {
          color: #002053;

          &::after, &::before {
            width: 50%;
          }
        }

        &::after {
          display: block;
          content: "";
          width: 0%;
          height: 2px;
          background-color: #002053;
          position: absolute;
          bottom: 13px;
          right: 50%;
          transition: .3s;
        }

        &::before {
          display: block;
          content: "";
          width: 0%;
          height: 2px;
          background-color: #002053;
          position: absolute;
          bottom: 13px;
          left: 50%;
          transition: .3s;
        }
      }
    }
  }
}

.head_gnb {
  width: 33.3333%;

  ul {
    display: flex;
    justify-content: flex-end;

    li {
      margin-right: 25px;
      position: relative;

      &:nth-child(4) {}

      .count {
        width: 20px;
        height: 20px;
        line-height: 20px;
        display: block;
        background-color: #002053;
        border-radius: 50%;
        text-align: center;
        color: #fff;
        font-size: 13px;
        position: absolute;
        top: 10px;
        right: -5px;
      }

      a {
        width: 100%;
        display: block;
        font-size: 25px;
        color: #fff;
        transition: .3s;
      }
    }
  }
}

.header:hover {
  .head_lnb ul li a, .head_gnb ul li a {
    color: black;
  }
}

/********drop down menu*********/

.dropdown_menu {
  width: 100%;
  position: absolute;
  top: 70px;
  z-index: 100;
  background-color: #fff;
  display: none;
  padding: 20px;
}

.drop_menu_wrap {
  width: 100%;
  display: flex;
}

.drop_kids .dbox {
  width: 20%;

  &:last-child {
    border: 0;
  }
}

.dbox {
  width: 16.66667%;
  border-right: 1px solid #ccc;
  padding-left: 30px;
  padding-top: 10px;

  h4 {
    font-size: 22px;
    margin-bottom: 25px;
  }
}

.dropbox_06 {
  border-right: 0;
}

.menu_list {
  width: 100%;

  li {
    height: 20px;
    margin-bottom: 20px;

    &:hover a {
      color: #002053;
      font-weight: bold;
    }

    a {
      display: block;
      width: 100%;
    }
  }
}

.sub_menu {
  margin-top: 50px;

  li {
    margin-bottom: 10px;
    font-weight: bold;

    a {
      display: block;
