        width: 25%;
        text-align: left;
        padding-left: 50px;

        .cs_box {
          border: 1px solid black;
          text-align: center;

          /* width: 50%; */
          height: 35px;
          line-height: 35px;
          font-size: 22px;
          border-radius: 30px;
          margin-bottom: 20px;
        }

        h4 {
          margin-top: 10px;
          font-size: 20px;
        }

        p.day {
          font-size: 14px;
          margin-top: 10px;
        }
      }

      .menu06_3 {
        width: 25%;

        i {
          font-size: 35px;
          margin-left: 30px;
          cursor: pointer;

          &:nth-child(1) {
            margin-left: 0;
          }
        }
      }
    }
  }
}

/**********quick menu***************/

.quick_menu {
  width: 55px;
  border: 1px solid #ccc;
  position: absolute;
  right: 0px;
  top: 0px;
  z-index: 99998;
  background-color: #fff;
  transition: .3s;

  &:hover {
    width: 150px;
  }

  .quick_wrap .quick_box {
    display: flex;
    border-bottom: 1px solid #ccc;
    height: 50px;
    line-height: 50px;

    &:nth-child(4) {
      border-bottom: 0;
    }

    &:nth-child(1) {}

    .quick_icon {
      width: 30%;
      text-align: center;

      i {
        font-size: 30px;
        color: #999;
        transition: .3s;
      }

      .usericon {
        padding-left: 11px;
        padding-top: 7px;
      }

      .truck {
        padding-left: 8px;
        padding-top: 10px;
      }

      .boxopen {
        padding-left: 7px;
        padding-top: 10px;
      }

      .quicksearch {
        padding-left: 10px;
        padding-top: 10px;
      }
    }

    &:hover i {
      color: #002053;
    }

    .quick_text {
      width: 0%;
      text-align: center;
      overflow: hidden;

      a {
        font-size: 16px;
        display: block;
        width: 100%;

        &:hover {
          color: #002053;
          font-weight: bold;
        }
      }
    }
  }

  &:hover .quick_wrap .quick_box .quick_text {
    /* display: block; */
    width: 70%;
  }
}

/*********dir btn*********/

.dir_btn {
  width: 50px;
  line-height: 50px;
  text-align: center;
  position: fixed;
  bottom: 100px;
  right: 0;
  cursor: pointer;
  z-index: 9999;

  .top_btn {
    width: 50px;
    height: 50px;
    font-size: 35px;
    color: #002053;
  }

  .bot_btn {
    width: 50px;
    height: 50px;
    font-size: 35px;
    color: red;
  }
}
