* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

a {
  text-decoration: none;
  color: black;
}

.container {
  width: 1440px;
  margin: 0 auto;
}

li {
  list-style: none;
}

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+KR:wght@300;400;500;700&display=swap');

/* font-family: 'Noto Sans KR', sans-serif; */

body {
  font-family: 'Noto Sans KR', sans-serif;
  font-weight: 500;
}

.search_box {
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(15px);
  position: fixed;
  top: 0px;
  z-index: 99999;
  display: none;

  .search_box_wrap {
    width: 100%;
    background-color: #fff;
    padding: 30px 0;
  }

  .search_input {
    width: 100%;
    position: relative;
    padding-top: 30px;

    .search_icon {
      width: 50px;
      height: 50px;
      font-size: 35px;
      position: absolute;
      top: 35px;
      left: 25px;
    }

    input {
      width: 90%;
      height: 60px;
      margin: 0 auto;
      display: block;
      border-radius: 30px;
      outline: none;
      border: 2px solid #ccc;
      background-color: #eee;
      font-size: 20px;
      padding: 0 15px;
      text-align: center;

      &[placeholder] {
        font-weight: 700;
      }
    }

    .search_close {
      width: 50px;
      height: 50px;
      font-size: 30px;
      position: absolute;
      right: 0;
      top: 35px;
      cursor: pointer;
    }
  }

  .search_list_wrap {
    display: flex;
    margin-top: 30px;

    .search_list {
      width: 33.3333%;
      border-right: 1px solid #ccc;
      padding-left: 100px;

      &:last-child {
        border-right: 0;
      }

      h4 {
        font-size: 20px;
        margin: 30px 0;
      }

      li {
        margin-bottom: 15px;
      }
    }

    .recent_search {
      li:first-child {
        color: #888;
      }

      .delete_history span {
        font-size: 14px;
        color: #888;
        text-decoration: underline;
        display: block;
        margin-top: 50px;
        cursor: pointer;
      }
    }
  }
}

/*************header*************/

header {
  width: 100%;
  position: fixed;
  top: 0;
  z-index: 999;
  height: 70px;
  background-color: rgba(0, 32, 83, 0.3);

  &:hover {
    background-color: #fff;

    a {
      color: black !important;
    }
  }
}

.head_up {
  position: absolute;
}

.head_down {
  position: fixed;
}

.header_wrap {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.head_logo {
  width: 33.3333%;

  .imgwrap {
    width: 90px;
    margin-left: 25px;
    margin-top: 10px;

    img {
      width: 100%;
      height: auto;
    }
  }
}

.head_lnb {
  width: 33.3333%;

  ul {
    display: flex;
    justify-content: space-around;
    line-height: 70px;

    li {
      font-size: 20px;
      font-weight: bold;

      &:nth-child(4) {
        position: relative;

        &::before {
          content: "";
          display: block;
          width: 1px;
          height: 14px;
          background-color: #ccc;
          position: absolute;
          top: 25px;
          left: -35px;
        }
      }

      a {
        display: block;
        width: 100%;
        color: #fff;
        position: relative;
        transition: .3s;

        &:hover {
          color: #002053;

          &::after, &::before {
            width: 50%;
          }
        }

        &::after {
          display: block;
          content: "";
          width: 0%;
          height: 2px;
          background-color: #002053;
          position: absolute;
          bottom: 13px;
          right: 50%;
          transition: .3s;
        }

        &::before {
          display: block;
          content: "";
          width: 0%;
          height: 2px;
          background-color: #002053;
          position: absolute;
          bottom: 13px;
          left: 50%;
          transition: .3s;
        }
      }
    }
  }
}

.head_gnb {
  width: 33.3333%;

  ul {
    display: flex;
    justify-content: flex-end;

    li {
      margin-right: 25px;
      position: relative;

      &:nth-child(4) {}

      .count {
        width: 20px;
        height: 20px;
        line-height: 20px;
        display: block;
        background-color: #002053;
        border-radius: 50%;
        text-align: center;
        color: #fff;
        font-size: 13px;
        position: absolute;
        top: 10px;
        right: -5px;
      }

      a {
        width: 100%;
        display: block;
        font-size: 25px;
        color: #fff;
        transition: .3s;
      }
    }
  }
}

.header:hover {
  .head_lnb > ul > li > a, .head_gnb > ul > li > a {
    color: black;
  }
}

/********drop down menu*********/

.dropdown_menu {
  width: 100%;
  position: absolute;
  top: 70px;
  z-index: 100;
  background-color: #fff;
  display: none;
  padding: 20px;
  border-bottom: 2px solid #ccc;
}

.drop_menu_wrap {
  width: 100%;
  display: flex;
}

.drop_kids .dbox {
  width: 20%;

  &:last-child {
    border: 0;
  }
}

.dbox {
  width: 16.66667%;
  border-right: 1px solid #ccc;
  padding-left: 20px;
  padding-top: 10px;

  h4 {
    font-size: 22px;
    margin-bottom: 25px;
  }
}

.dropbox_06 {
  border-right: 0;
}

.menu_list {
  width: 100%;

  li {
    height: 20px;
    margin-bottom: 20px;

    a {
      &:hover {
        font-weight: bold;
        color: red !important;
      }

      display: block;
      width: 100%;
    }
  }
}

.sub_menu {
  margin-top: 50px;

  li {
    margin-bottom: 20px;
    font-weight: bold;
    height: 20px;

    a {
      display: block;
      width: 100%;
      position: relative;
    }

    &:hover a {
      color: red !important;
    }
  }
}

.drop_brand .drop_menu_wrap .brand_box {
  width: calc(100% - 80% - 24px);
  margin: 50px 20px;

  .brand_img {
    width: 100%;
    overflow: hidden;
    font-size: 0;

    img {
      width: 100%;
      height: auto;
      transform: scale(1.15);
      transition: .5s;
    }

    &:hover img {
      transform: scale(1);
    }
  }

  .brand_tit {
    text-align: center;
    margin-top: 20px;
    font-size: 20px;
  }
}

/***********section01 detail view************/

.section01 {
  width: 100%;
  margin-top: 100px;
  padding: 0 20px;
}

.detail_wrap {
  display: flex;

  .detail_box_L {
    width: 70%;

    /* height: 900px; */

    .box_L_wrap {
      display: flex;

      .detail_video {
        width: calc(100% - 50% - 10px);
        margin-right: 20px;
        font-size: 0;

        video {
          width: 100%;
          height: auto;
        }
      }

      .detail_thumb {
        width: calc(100% - 50% - 10px);
      }
    }
  }
}

/********banner**********/

.banner_wrap {
  width: 100%;
  margin-top: 30px;
  position: relative;

  .bannerimg_wrap {
    width: 100%;
    height: 200px;

    img {
      width: 100%;
      height: 100%;
    }
  }
}

.banner_text {
  position: absolute;
  top: 50%;
  left: 10%;
  font-size: 20px;
  transform: translateY(-50%);
  font-size: 30px;

  span {
    color: #03398f;
  }
}

.swiper01 {
  width: 100%;
  height: auto;
  position: relative;

  .swiper-pagination {
    position: absolute;
    width: 100%;
  }

  .swiper-pagination-bullet {
    width: 16%;
    height: 100px;
    border-radius: 0;
    background-color: #fff;
    border: 1px solid #ccc;
    opacity: .5;

    /* transition: .3s; */
  }

  .swiper-pagination-bullet-active {
    border: 0;
    border: 3px solid #002053;
    opacity: 1;

    &:nth-child(5), &:nth-child(4) {
      border: 3px solid #eee;
    }
  }

  .swiper-pagination-bullet {
    &:nth-child(1) {
      background: url(../img/detail01.jpg) no-repeat center center;
      background-size: cover;

      /* background-position: 50% 0; */
    }

    &:nth-child(2) {
      background: url(../img/detail02.jpg) no-repeat center center;
      background-size: cover;
    }

    &:nth-child(3) {
      background: url(../img/detail03.jpg) no-repeat center center;
      background-size: cover;
    }

    &:nth-child(4) {
      background: url(../img/detail04.jpg) no-repeat center center;
      background-size: cover;
    }

    &:nth-child(5) {
      background: url(../img/detail05.jpg) no-repeat center center;
      background-size: cover;
    }
  }
}

.swiper01_ex {
  overflow: hidden;
}

.swiper01 .slide01 {
  width: 100%;
  height: auto;
  font-size: 0;

  img {
    width: 100%;
    height: auto;
  }
}

.detail_wrap .detail_box_R {
  width: 30%;

  /* height: 900px; */
  border: 1px solid #ccc;
  padding: 0 10px;

  .category p {
    display: inline-block;
    margin-right: 10px;
    font-size: 14px;

    &::after {
      content: "";
      width: 1px;
      height: 15px;
      display: inline-block;
      background-color: #999;
      vertical-align: middle;
      margin-left: 10px;
    }

    &:last-child:after {
      visibility: hidden;
    }
  }
}

.category_wrap {
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
  color: #999;
}

.category_02 i {
  font-size: 23px;
  cursor: pointer;
  color: black;
}

.box_R_tit {
  margin-top: 10px;
}

.box_R_color .color_wrap {
  overflow: hidden;
  margin-top: 30px;
}

.swiper02 {
  margin-left: 30px;

  ul li {
    width: 28% !important;

    /* height: 150px; */
    font-size: 0;
    cursor: pointer;
  }

  li img {
    width: 100%;
    height: 100%;
  }
}

.size_box {
  margin-top: 60px;

  .size_wrap .size_tit {
    display: flex;
    justify-content: space-between;

    h4 {
      font-size: 18px;
    }

    p {
      text-decoration: underline;
      cursor: pointer;
    }
  }
}

.size_chkbox {
  margin-top: 20px;

  ul {
    display: flex;
    justify-content: space-between;

    li {
      width: calc(100% - 75% - 7.5px);
      height: 60px;
      border: 1px solid #ccc;
      text-align: center;
      line-height: 60px;
      border-radius: 10px;

      label {
        display: block;
        width: 100%;
        height: 100%;
        transition: .3s;
      }
    }
  }
}

#size_chk01, #size_chk02, #size_chk03, #size_chk04 {
  display: none;
}

#size_chk01:checked ~ .size_label01, #size_chk02:checked ~ .size_label02, #size_chk03:checked ~ .size_label03, #size_chk04:checked ~ .size_label04 {
  background-color: #002053;
  color: #fff;
  border-radius: 10px;
}

.count_pdc {
  margin-top: 80px;

  .count_wrap {
    display: flex;
    text-align: center;
    border: 1px solid #ccc;
    height: 65px;
    line-height: 65px;
    font-size: 20px;
    border-radius: 10px;

    .minus_box {
      width: 25%;
      cursor: pointer;
      transition: .3s;
      border-radius: 10px 0 0 10px;

      &:hover {
        color: #fff;
        background-color: #002053;
      }
    }

    .cnt_box {
      width: 50%;

      input {
        text-align: center;
        color: black;
        border: 0;
        width: 100%;
        font-size: 18px;
        font-weight: bold;
        outline: none;

        &[placeholder] {
          color: black;
        }
      }
    }

    .plus_box {
      &:hover {
        color: #fff;
        background-color: #002053;
      }

      width: 25%;
      cursor: pointer;
      transition: .3s;
      border-radius: 0 10px 10px 0;
    }
  }
}

.notallow {
  cursor: not-allowed;
}

.price_pdc {
  margin-top: 70px;

  .price_wrap {
    display: flex;
    justify-content: space-between;

    h4, p {
      font-size: 18px;
    }
  }
}

.buy_pdc {
  margin-top: 50px;

  .buy_wrap {
    display: flex;
    flex-wrap: wrap;
    text-align: center;
    line-height: 50px;

    .like_box {
      width: calc(50% - 10px);
      border: 1px solid #ccc;
      margin-right: 20px;
      border-radius: 20px;
      font-size: 28px;
      transition: .3s;
      cursor: pointer;

      &:hover {
        background-color: #002053;
        color: red;
        box-shadow: 0 0 10px black;
        border: 1px solid #002053;
      }
    }

    .cart_box {
      width: calc(50% - 10px);
      border: 1px solid #ccc;
      height: 50px;
      border-radius: 20px;
      font-size: 28px;
      transition: .3s;
      cursor: pointer;

      &:hover {
        background-color: #002053;
        color: #fff;
        box-shadow: 0 0 10px black;
        border: 1px solid #002053;
      }
    }

    .buy_box {
      width: 100%;
      margin-top: 30px;

      &:hover button {
        background-color: #002053;
        color: #fff;
        box-shadow: 0 0 10px black;
        border: 1px solid #002053;
      }

      button {
        width: 100%;
        border: 1px solid #ccc;
        background-color: #fff;
        cursor: pointer;
        height: 60px;
        border-radius: 20px;
        font-size: 18px;
        font-weight: 600;
        color: #002053;
        transition: .3s;
      }
    }
  }
}

/********section02 상품정보고시 ~***********/

.section02 {
  width: 100%;
}

.product_info_wrap {
  margin-top: 100px;

  .pdc_info_tit ul {
    display: flex;

    li {
      width: 200px;
      height: 70px;

      /* border: 1px solid #ccc; */
      background-color: #eee;
      border-radius: 20px 20px 0 0;
      line-height: 70px;
      text-align: center;
      font-size: 20px;
      transition: .3s;
      cursor: pointer;

      &.on {
        background-color: #002053;
        color: #fff;
      }
    }
  }
}

.information_box {
  border: 1px solid #ccc;
}

.infobox_02, .infobox_03 {
  display: none;
}

.information_box .information_box_wrap {
  display: flex;
  align-items: center;
  border-bottom: 1px solid #ccc;

  .infobox_tit {
    width: 30%;

    h4 {
      text-align: left;
      padding-left: 20px;
      font-size: 20px;
    }
  }

  .infobox_desc {
    width: 70%;
    padding: 20px 30px;

    p {}
  }
}

.detail_desc .detail_desc_tit h4 {
  font-size: 20px;
}

.detail_desc_wrap .detail_desc_box {
  display: flex;
  align-items: center;
  border-bottom: 1px solid #ccc;

  &:last-child {
    border-bottom: 0;
  }

  .detail_sub_tit {
    width: 30%;
    text-align: left;
    padding-left: 20px;

    h4 {
      font-size: 20px;
    }
  }

  .detail_txt {
    width: 70%;
    padding: 20px 30px;
  }
}

/**********section3 리뷰 후기***********/

.section03 {
  width: 100%;
  margin-top: 100px;
}

.review_section_wrap .review_tab ul {
  display: flex;

  li {
    width: 200px;
    height: 70px;

    /* border: 1px solid #ccc; */
    background-color: #eee;
    border-radius: 20px 20px 0 0;
    line-height: 70px;
    text-align: center;
    font-size: 20px;
    transition: .3s;
    cursor: pointer;

    &.on1 {
      background-color: #002053;
      color: #fff;
    }
  }
}

.review_box {
  /* border-top: 1px solid #ccc; */

  .review_box_wrap {
    display: flex;
    border: 1px solid #ccc;

    .review_img {
      width: 25%;
      padding: 10px;

      img {
        width: 100%;
        height: 100%;
      }
    }

    .review_desc {
      width: 75%;
      padding: 0 10px;

      .review_tit {
        display: flex;
        justify-content: space-between;
        margin-top: 20px;

        h4 {
          font-size: 22px;
        }

        button {
          width: 200px;
          height: 50px;
          background-color: #fff;
          border: 0;
          border-radius: 30px;
          border: 1px solid #ccc;
          font-size: 18px;
          font-weight: 600;
          cursor: pointer;
          transition: .3s;

          &:hover {
            background-color: #002053;
            color: #fff;
            box-shadow: 0 0 10px black;
            border: 1px solid black;
          }
        }
      }

      .review_text {
        width: 100%;

        /* margin-top:10px */

        .review_info {
          display: flex;
          justify-content: flex-start;

          .review_name input {
            height: 35px;
            width: 250px;
            outline: none;
            padding: 0 5px;
          }
        }

        .review_txtbox {
          width: 100%;

          textarea {
            width: 100%;
            height: 340px;
            margin-top: 20px;
            outline: none;
            font-size: 18px;
            resize: none;
            padding: 10px;
          }
        }
      }
    }
  }
}

