.review_box .review_box_wrap .review_desc .review_text .review_info .review_passwd input{
    height: 35px;
    width: 250px;
    outline: none;
    padding: 0 5px;
} */

.review_star {
  width: 200px;
  height: 35px;
  margin-left: 20px;
  font-size: 30px;
  color: #ccc;
  text-align: center;
  line-height: 35px;
}

/***************************/

.review_filter {
  width: 100%;
  padding: 0 10px;
  margin-top: 20px;

  ul {
    display: flex;

    li {
      margin-right: 20px;

      input {
        display: none;
      }

      label {
        font-size: 20px;
        transition: .3s;
        color: #999;
      }
    }
  }
}

#review_chk01:checked ~ .review_label01 {
  color: #002053;
  text-shadow: 0 0 10px black;
}

#review_chk02:checked ~ .review_label02, #review_chk03:checked ~ .review_label03 {
  color: black;
  text-shadow: 0 0 10px #002053;
}

/******review list*********/

.review_list {
  width: 100%;
  padding: 0 10px;
  margin-top: 10px;

  .review_list_wrap .review {
    width: 100%;
    height: 80px;
    line-height: 80px;

    /* border-top: 1px solid #ccc; */
    /* border-bottom: 1px solid #ccc; */

    .review_inner {
      display: flex;
      text-align: center;
      border-top: 1px solid #ccc;

      .inner_stars {
        width: 15%;
      }

      .inner_txt {
        width: 60%;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
        padding: 0 20px;
      }

      .inner_id {
        width: 10%;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
      }

      .inner_date {
        width: 7%;
      }

      .inner_close {
        width: 3%;
        cursor: pointer;
      }

      .inner_number {
        width: 5%;
      }
    }
  }
}

/***********notice list************/

.notice_list {
  width: 100%;
  padding: 0 10px;
  margin-top: 10px;
  cursor: pointer;

  .notice_list_wrap .notice {
    width: 100%;
    height: 80px;
    line-height: 80px;

    .notice_inner {
      display: flex;
      text-align: center;
      border-bottom: 1px solid #ccc;
      flex-wrap: wrap;

      .notice_num {
        width: 10%;
      }

      .notice_txt {
        width: 55%;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
        padding: 0 20px;
      }

      .notice_rebtn {
        width: 10%;
        cursor: pointer;
      }

      .notice_id {
        width: 10%;
        text-overflow: ellipsis;
        overflow: hidden;
        white-space: nowrap;
      }

      .notice_date {
        width: 10%;
      }

      .notice_close {
        width: 5%;
        cursor: pointer;
      }
    }
  }
}

.notice_box01 {
  display: none;
}

.review_box01 {
  display: block;
}

/***********popup 실측사이즈************/

.real_size {
  width: 100%;
  height: 100vh;
  position: fixed;
  top: 0;
  z-index: 9999;
  display: none;
}

.real_bgc {
  width: 100%;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.6);
  position: relative;
}

.real_wrap {
  width: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  img {
    width: 100%;
    height: auto;
  }
}

.size_close {
  font-size: 50px;
  position: absolute;
  top: 150px;
  right: 300px;
  color: #fff;
  cursor: pointer;
}

/*********답글**********/

.re_pop {
  width: 100%;
  height: 100vh;
  position: fixed;
  top: 0;
  z-index: 99999;
  display: none;
}

.re_bgc {
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  position: relative;
}

.re_wrap {
  width: 50%;
  height: 50%;
  border: 1px solid #ccc;
  background-color: #fff;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  h4 {
    text-align: center;
    font-size: 24px;
